mapper.select:
UPDATE post
SET post_writer= #{post_writer}, post_title = #{post_title}, post_content = #{post_content}, post_per= #{post_per}, post_tag = #{post_tag}
WHERE post_id = #{post_id}

mapper.update:
UPDATE post
SET post_view = post_view + 1
WHERE post_id = #{post_id}

mapper.select:
SELECT A.*, e.emp_name
FROM (
    SELECT p.*, ROWNUM RNUM
    FROM post p
    WHERE p.parent_board = #{parent_board}
    ORDER BY p.post_id DESC
) A
JOIN employee e ON A.post_writer = e.emp_code_id  
WHERE ROWNUM <= #{offset} + #{size}
AND RNUM > #{offset}
